def minimax(player, game, time_left, depth, my_turn=True):
    """Implementation of the minimax algorithm.
    Args:
        player (CustomPlayer): This is the instantiation of CustomPlayer()
            that represents your agent. It is used to call anything you
            need from the CustomPlayer class (the utility() method, for example,
            or any class variables that belong to CustomPlayer()).
        game (Board): A board and game state.
        time_left (function): Used to determine time left before timeout
        depth: Used to track how deep you are in the search tree
        my_turn (bool): True if you are computing scores during your turn.

    Returns:
        (tuple, int): best_move, val
    """

    # TODO: finish this function!

    #raise NotImplementedError
    #print(game)
    if my_turn == True:
        player = game.get_active_player()
        player_moves = game.get_player_moves(player)
        if depth == 0 or time_left == 0: #must terminate at depth = 0 or when the time is up
            return None, len(player_moves) - len(game.get_opponent_moves(player)) #need to return a null state/move
        best_move = None
        highest_value = -100000 #need a very small number that I know the board wont generate
        for move in player_moves:
            forecast = game.forecast_move(move)
            forecast_board = forecast[0]
            forecast_win = forecast[1]
            if forecast_win == True:
                #this means our tree has found a path to win
                return move, 100000
            output = minimax(player, forecast_board, time_left, depth-1, my_turn=False)
            if output[1] > highest_value:
                highest_value = output[1]
                best_move = move
        return best_move, highest_value
    elif my_turn == False:
        player = game.get_inactive_player() #grabs opponent to simulate moves
        opponent_moves = game.get_opponent_moves(player)
        if depth == 0 or time_left == 0:
            return None, len(game.get_player_moves(player)) - len(opponent_moves)
        best_move = None
        lowest_value = 100000
        for move in opponent_moves:
            forecast = game.forecast_move(move)
            forecast_board = forecast[0]
            forecast_win = forecast[1]
            if forecast_win == True:
                return move, -100000
            output = minimax(player, forecast_board, time_left, depth-1, my_turn=True)
            if output[1] < lowest_value:
                lowest_value = output[1]
                best_move = move
        return best_move, lowest_value

