def alphabeta(player, game, time_left, depth, alpha=float("-inf"), beta=float("inf"), my_turn=True):
    """Implementation of the alphabeta algorithm.

    Args:
        player (CustomPlayer): This is the instantiation of CustomPlayer()
            that represents your agent. It is used to call anything you need
            from the CustomPlayer class (the utility() method, for example,
            or any class variables that belong to CustomPlayer())
        game (Board): A board and game state.
        time_left (function): Used to determine time left before timeout
        depth: Used to track how deep you are in the search tree
        alpha (float): Alpha value for pruning
        beta (float): Beta value for pruning
        my_turn (bool): True if you are computing scores during your turn.

    Returns:
        (tuple, int): best_move, val
    """

    # TODO: finish this function!
    #raise NotImplementedError
    if my_turn == True:
        player = game.get_active_player()
        player_moves = game.get_player_moves(player)
        if depth == 0 or time_left() < 100: #just return the best move we have if we run out of time/depth
            return None, player.utility(game, True)

        best_move = player_moves[0]
        highest_value = -100000 #need a value that I know the board wont generate
        for move in player_moves:
            forecast = game.forecast_move(move)
            forecast_board = forecast[0]
            forecast_win = forecast[1]
            if forecast_win == True:
                return move, 100000
            output = alphabeta(player, forecast_board, time_left, depth - 1, alpha, beta, my_turn=False)
            if output[1] > highest_value:
                highest_value = output[1]
                best_move = move
                alpha = max(alpha, highest_value)
                #alpha = output[1]

            if highest_value >= beta:
                return best_move, highest_value
        return best_move, highest_value
    elif my_turn == False:
        player = game.get_inactive_player()
        opponent_moves = game.get_opponent_moves(player)
        if depth == 0 or time_left() < 100:
            return None, player.utility(game, False)
        worst_move = opponent_moves[0]
        lowest_value = 100000
        for move in opponent_moves:
            forecast = game.forecast_move(move)
            forecast_board = forecast[0]
            forecast_win = forecast[1]
            if forecast_win == True:
                return move, -100000
            output = alphabeta(player, forecast_board, time_left, depth-1, alpha,beta, my_turn=True)
            if output[1] < lowest_value:
                lowest_value = output[1]
                worst_move = move
                beta = min(beta, lowest_value)
                #beta = lowest_value
            if lowest_value <= alpha:
                return worst_move, lowest_value
        return worst_move, lowest_value




